import React, {Component} from 'react';

import Cards from "./cards/cards";
import './questions.css';
import CTA from "../global/button/CTA";


class Questions extends Component {
    submitQuestions;
    handleClick;

    constructor(props) {
        super(props);
        this.state = {
            questions: ''
        }
    };

    render() {

        


        this.submitQuestions = (event) => {
            event.preventDefault();
            console.log(document.getElementsByClassName('boxQuestion').length);
            let questions = [];
            window.addEventListener('load', () => {
                let questionNumber = 2;

                for (let i = 1; i <= questionNumber; i++) {
                    questions.push(<Cards
                        id={i.toString()}
                        title={"Question " + i + ":"}
                        label={"answer" + i.toString()}
                        onClick={this.handleClick}

                    />);
                }

                this.setState({
                    questions: questions
                });
            });

            return (
                <section className="questionnaire">
                    <form onSubmit={this.submitQuestions}>
                        {this.state.questions}
                        <button type="submit" value="Etape suivante">Etape suivante</button>
                    </form>
                </section>
            )
        }
    }
}

export default Questions;
