import React, {Component} from 'react';
import './signUp.css';

import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faChevronDown} from "@fortawesome/free-solid-svg-icons";

const axios = require('axios');

class SignUp extends Component {
    handleSubmit;

    render() {

        this.handleSubmit = () => {
            event.preventDefault();

            let $email = document.getElementById('email').value;
            let $confirmEmail = document.getElementById('confirmEmail').value;
            let $userName = document.getElementById('username').value;
            let $password = document.getElementById('password').value;
            let $confirmPassword = document.getElementById('confirmPassword').value;

            if ($email && $confirmEmail && $userName && $password && $confirmPassword !== '') {
                if ($email === $confirmEmail) {
                    if ($password === $confirmPassword) {

                        axios.post('/api')
                            .then((req) => {
                                console.log(req)
                            })
                            .catch((error) => {
                            })

                    } else {
                        console.log('Mots de passe pas identique')
                    }
                } else {
                    console.log('Email pas identiques')
                }
            } else {
                console.log('No');
            }


        };

        return (
            <section className="signUpSection">

                <form method="post" onSubmit={this.handleSubmit} className="formSignUp">
                    <h1>Inscription</h1>
                    <span>Inscrivez-vous !</span><br/>
                    <input type="email" id="email" placeholder="Email"/><br/>
                    <input type="email" id="confirmEmail" placeholder="Confirmer l'E-mail"/><br/>
                    <input type="text" id="username" placeholder="Nom d'utilisateur"/><br/>
                    <input type="password" id="password" placeholder="Mot de passe"/><br/>
                    <input type="password" id="confirmPassword" placeholder="Confirmer le mot de passe"/><br/>
                    <input type="button" id="submitSignUp" value="Prochaine étape"/><br/>
                </form>

                <div className="accordeon">
                    <div className="spanSpace" id="chevron">
                        <span>Quelles sont les informations requises pour ouvrir un compte Clausius Investments ?</span><FontAwesomeIcon
                        className="fasChevron" icon={faChevronDown}/><br/>
                    </div>
                    <div className="spanSpace" id="chevron">
                        <span>Quelles sont les informations requises pour ouvrir un compte Clausius Investments ?</span><FontAwesomeIcon
                        className="fasChevron" icon={faChevronDown}/><br/>
                    </div>
                    <div className="spanSpace" id="chevron">
                        <span>Quelles sont les informations requises pour ouvrir un compte Clausius Investments ?</span><FontAwesomeIcon
                        className="fasChevron" icon={faChevronDown}/>
                    </div>
                </div>
                <div className="interactions">
                    <span><u>CREER LE COMPTE</u></span><br/><br/>
                    <span>Vous avez déjà un compte ? Connectez-vous ici</span>
                </div>
            </section>
        );
    }
}

export default SignUp
