import React, {Component} from 'react';

import Cards from "./cards/cards";
import './questions.css';

import CTA from "../global/button/CTA";


class Questions extends Component {
    submitQuestions;
    handleClick;
    answers;

    constructor(props) {
        super(props);
        this.state = {
            questions: '',
        }
    };

    render() {

        this.answers = [];

        this.handleClick = (event) => {
            for (let i = 0; i <= this.answers[i]; i++) {
                let value = event.target.value;
                if (this.answers[i] === null) {
                    this.answers.push(value);
                    
                } else {

                }
            }

        };

        this.submitQuestions = (event) => {
            event.preventDefault();
            let lengthBox = document.getElementsByClassName('boxQuestion');

            for (let i = 0; i <= this.answers.length; i++) {

                if (lengthBox.length === this.answers.length) {
                    console.log('ok')
                } else {
                    console.log(lengthBox.length);
                    console.log(this.answers.length)
                }


            }


        };

        let questions = [];

        window.addEventListener('load', () => {
            let questionNumber = 2;

            for (let i = 1; i <= questionNumber; i++) {
                questions.push(<Cards
                    id={i.toString()}
                    title={"Question " + i + ":"}
                    label={"answer" + i.toString()}
                    onClick={this.handleClick}
                />);
            }

            this.setState({
                questions: questions
            });
        });

        return (
            <section className="questionnaire">
                <form onSubmit={this.submitQuestions}>
                    {this.state.questions}
                    <button type="submit" value="Etape suivante">Etape suivante</button>
                </form>
            </section>
        )
    }
}

export default Questions;
