import React, {Component} from 'react';

import './leftBar.css'


class LeftBar extends Component {
    changeBackground;

    constructor(props) {
        super(props);
        this.state = {
            firstLiClass: '',
            secondLiClass: '',
            thirdLiClass: '',
            fourthLiClass: '',
        };

        this.refOnScroll = React.createRef();
    }


    componentDidMount() {
        this.changeBackground()
    }

    render() {

        this.changeBackground = () => {
            let url = window.location.href;

          switch (url) {
              case "/app/ordres":

              case "/app/transactions":

              case "/app/soldes":

              case "/app/bot":


          }
        };

        return (
            <div className="leftBar">
                <ul>
                    <li>Achat</li>
                    <li>Ordres</li>
                    <li>Solde</li>
                    <li>Bot de la semaine</li>
                </ul>
            </div>


        )
    }
}

export default LeftBar;
