import React, {Component} from 'react';

import Cards from "./cards/cards";
import './questions.css';

import CTA from "../global/button/CTA";


class Questions extends Component {
    submitQuestions;
    handleClick;
    answers;
    answersA;
    answersB;
    answersC;
    answersD;
    count;

    constructor(props) {
        super(props);
        this.state = {
            questions: '',
        }
    };

    render() {

        this.answers = [];


        this.handleClick = (event) => {
            let value = event.target.value;
            this.answers.push(value);
        };

        function answerA(element) {
            return element >= "A";
        }

        function answerB(element) {
            return element >= "B";
        }

        function answerC(element) {
            return element >= "B";
        }

        function answerD(element) {
            return element >= "B";
        }

        this.submitQuestions = (event) => {
            event.preventDefault();
            let lengthBox = document.getElementsByClassName('boxQuestion').length;
            if (this.answers.length === lengthBox) {
                this.count = this.answers.filter(answerA);

                let numOfA = this.answers.filter(function (x) {
                    return x === "A";
                }).length;
                console.log(numOfA);

                let numOfB = this.answers.filter(function (x) {
                    return x === "B";
                }).length;
                console.log(numOfB);


            } else if (this.answers.length > lengthBox) {
                alert("Probleme avec vos réponses, la page va rechargé.");
                document.location.reload(true);
            } else if (this.answers.length < lengthBox) {
                alert("Toutes les réponses n'ont pas été enrégistré !")
            }

        };

        let questions = [];

        window.addEventListener('load', () => {
            let questionNumber = 4;

            for (let i = 1; i <= questionNumber; i++) {
                questions.push(<Cards
                    id={i.toString()}
                    title={"Question " + i + ":"}
                    label={"answer" + i.toString()}
                    onClick={this.handleClick}
                />);
            }

            this.setState({
                questions: questions
            });
        });

        return (
            <section className="questionnaire">
                <form onSubmit={this.submitQuestions}>
                    {this.state.questions}
                    <button type="submit" value="Etape suivante">Etape suivante</button>
                </form>
            </section>
        )
    }
}

export default Questions;
