import React from "react";
import Slider from "react-slick";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './carousel.css';


let settings;

/**
 * @class SimpleSlider permet d'importer un carousel slick slider et de définir tout ce que l'on veut dans ce dernier: <br>
 *     <a href="https://react-slick.neostack.com/docs/api/"> Voir les méthodes et props</a>
 */

class SimpleSlider extends React.Component {

    render() {
        settings = {
            infinite: true,
            speed: 500,
            slidesToShow: 2,
            slidesToScroll: 1
        };

        return (
            <div className="displaySlider">
                <Slider {...settings}>
                    <div className="firstslide">
                        <div className="slogan">
                            Gagnez du temps et de l'argent
                        </div>
                        <div className="catchWord">
                            <div className="catch">
                                Trouvez tous vos produits de pharmacie autour de vous
                            </div>
                            <div className="catch">
                                Déplacez-vous à la pharmacie ou optez pour la livraison
                            </div>
                        </div>
                    </div>
                    <div>
                        <h3>2</h3>
                    </div>
                    <div>
                        <h3>3</h3>
                    </div>
                    <div>
                        <h3>4</h3>
                    </div>
                    <div>
                        <h3>5</h3>
                    </div>
                </Slider>
            </div>
        );
    }
}

export default SimpleSlider;
