import React, {Component} from 'react';
import './header.css';

import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faBars} from "@fortawesome/free-solid-svg-icons";

import Clausius from '../../../assets/logo-idv-blanc.png'
import CTA from "../global/button/CTA";
import Liberty from "../../../assets/illustrations/offers/liberté.svg";


class Header extends Component {
    handleSlideLink;
    constructor(props) {
        super(props);
        this.state = {
            slideLink: "slidingLink",
        };
    }


    render() {

        this.handleSlideLink = () => {

            this.setState({
                slidingLink: "slidingLink flex"
            })
        };

        return (
            <header>
                <div className="logoClausius">
                    <img src={Clausius} alt="Logo Clausius Investment"/>
                </div>
                <nav>
                    <ul>
                        <li>Accueil</li>
                        <li>Équipe</li>
                    </ul>
                </nav>
                <div className="positionActions">
                    <div className="navActions">
                        <CTA className="navButtons" content="Se connecter"/>
                        <CTA className="navButtons" content="S'inscrire"/>
                    </div>
                </div>
                <div className="awsBarsBox">
                    <FontAwesomeIcon className="awesomeBars" icon={faBars} onClick={this.handleSlideLink}/>
                </div>
                <div className={this.state.slideLink}>
                    <ul>
                        <div className="webPages">
                            <h4>Web Pages</h4>
                            <li>Accueil</li>
                            <li>Equipe</li>
                        </div>
                        <div className="appPages">
                            <h4>App Pages</h4>
                            <li>Inscription</li>
                            <li>Connexion</li>
                        </div>
                    </ul>
                </div>
            </header>

        );
    }
}

export default Header
